    //Type appropriate comment here, and begin script below
	var booking_id = g_form.getValue('booking_id');
	jslog('booking id from form : '+ booking_id);
    var ga = new GlideAjax('x_651162_deliver_0.DeliveryExpressUtils');
    ga.addParam('sysparm_name', 'getCurrentState');
    ga.addParam('sysparm_booking_id', booking_id);
    ga.getXMLAnswer(process);

    function process(response) {
		var obj = JSON.parse(response);
		jslog('response : '+JSON.stringify(response));
		jslog('parsed response : '+obj[0].sys_id + ', ' + obj[0].state_name);
		controlForm(obj[0].state_name.toString());
    }

