sys_ui_action.script:
var c_state = current.current_state;
var ga = new GlideRecord('x_651162_deliver_0_state_details');
ga.addQuery('sys_id', c_state);
ga.setLimit(1);
ga.query();

while (ga.next()) {
	c_state = ga.getValue('state_name').toString();
}

gs.info('existing state : ' + c_state);
var t_state = '';
switch (c_state) {
    case 'f9f3a0e32f6230109e77bcb62799b66a':
        t_state = '5984a8e32f6230109e77bcb62799b631';
        break;
    case '5984a8e32f6230109e77bcb62799b631':
        t_state = '259460e32f6230109e77bcb62799b6b8';
        break;
    case '259460e32f6230109e77bcb62799b6b8':
        t_state = '88a460e32f6230109e77bcb62799b6fa';
        break;
    case '88a460e32f6230109e77bcb62799b6fa':
        t_state = '7ea4e8e32f6230109e77bcb62799b637';
        break;
    default:
        t_state = 'f9f3a0e32f6230109e77bcb62799b66a';
        break;
}
var gr = new GlideRecord('x_651162_deliver_0_state_details');
gr.newRecord();
gr.setValue('booking_id', current.booking_id);
gr.setValue('state_name', t_state);
gr.insert();

current.current_state = gr.getValue('sys_id');

current.update();
gs.info('new state : ' + current.current_state);
action.setRedirectURL(current);
